    private void bt_calculoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bt_calculoActionPerformed
        double peso;
        double alt;
        double result;
        
        peso = Double.parseDouble(txf_peso.getText());
        alt = Double.parseDouble(txf_altura.getText());
        
        result = (peso / (alt * alt));
        
        
        lbl_resultado.setText(Double.toString(result));
        
    }//GEN-LAST:event_bt_calculoActionPerformed

